document.addEventListener("DOMContentLoaded",function(){let e=document.querySelectorAll("details");e.forEach(t=>{t.addEventListener("click",()=>{e.forEach(e=>{e!==t&&e.removeAttribute("open")})})}),document.querySelector(".faq-btn").addEventListener("click",function(){let e=document.querySelector(".d-display");e.classList.toggle("show"),e.classList.contains("show")&&setTimeout(()=>{e.scrollIntoView({behavior:"smooth"})},90)})});